module.exports = {
    error: (body) => {
        return {
            "statusCode": "500",
            "errorMessage": body.message,
            "errorType": "internal",
            "body": JSON.stringify({
                data: null,
                error: body.message
            })
          };
    },
    success: (body) => {
        return {
            "statusCode": "200",
            "body": JSON.stringify({
                data: body
            })
          };
    }
}